  activeStep = signal(1);

  steps: DemoStep[] = [
    {
      id: 1,
      title: 'Sube tu PDF',
      description: 'Arrastra y suelta tu archivo PDF de examen. Soportamos hasta 200 p치ginas y 20MB.',
      visual: 'upload'
    },
    {
      id: 2,
      title: 'IA extrae preguntas',
      description: 'Nuestros algoritmos analizan el contenido y identifican preguntas y respuestas autom치ticamente.',
      visual: 'processing'
    },
    {
      id: 3,
      title: 'Revisa y edita',
      description: 'Valida las preguntas extra칤das, realiza ajustes y a침ade tus propias preguntas.',
      visual: 'editing'
    },
    {
      id: 4,
      title: 'Publica y comparte',
      description: 'Configura tu examen, establece tiempos y comp치rtelo con tus estudiantes.',
      visual: 'sharing'
