  background: var(--primary-gradient);
  overflow: hidden;
}

.cta-container {
  max-width: var(--container-max-width);
  margin: 0 auto;
  padding: var(--section-padding) var(--container-padding) 0;
  position: relative;
  z-index: 1;
}

.cta-background {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  overflow: hidden;
}

.bg-circle {
