import { Component, ChangeDetectionStrategy, signal } from '@angular/core';
import { CommonModule } from '@angular/common';

interface DemoStep {
  id: number;
  title: string;
  description: string;
  visual: string;
}

@Component({
  selector: 'app-demo-section',
  templateUrl: './demo-section.html',
  styleUrls: ['./demo-section.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  imports: [CommonModule]
})
export class DemoSectionComponent {

  activeStep = signal(1);

  steps: DemoStep[] = [
    {
      id: 1,
      title: 'Sube tu PDF',
      description: 'Arrastra y suelta tu archivo PDF de examen. Soportamos hasta 200 p치ginas y 20MB.',
      visual: 'upload'
    },
    {
      id: 2,
      title: 'IA extrae preguntas',
      description: 'Nuestros algoritmos analizan el contenido y identifican preguntas y respuestas autom치ticamente.',
      visual: 'processing'
    },
    {
      id: 3,
      title: 'Revisa y edita',
      description: 'Valida las preguntas extra칤das, realiza ajustes y a침ade tus propias preguntas.',
      visual: 'editing'
    },
    {
      id: 4,
      title: 'Publica y comparte',
      description: 'Configura tu examen, establece tiempos y comp치rtelo con tus estudiantes.',
      visual: 'sharing'
    }
  ];

  selectStep(stepId: number): void {
    this.activeStep.set(stepId);
  }

  getVisualDescription(): string {
    const step = this.steps.find(s => s.id === this.activeStep());
    switch (step?.visual) {
      case 'upload':
        return 'Demostraci칩n visual del proceso de subida de archivo PDF con barra de progreso';
      case 'processing':
        return 'Animaci칩n del cerebro artificial procesando el documento con ondas de actividad';
      case 'editing':
        return 'Interfaz de edici칩n mostrando preguntas extra칤das con opciones de respuesta';
      case 'sharing':
        return 'Tarjeta de examen publicado con estad칤sticas y opciones de compartir';
      default:
        return 'Demostraci칩n visual del paso actual del proceso';
    }
  }
}

