import { Component, output, input, ChangeDetectionStrategy, inject, signal } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatSelectModule } from '@angular/material/select';
import { SubmitButtonComponent } from '../../../../shared/components/submit-button';
import { RegisterRequest } from '../../../../core/models/user.model';

@Component({
  selector: 'app-register-form',
  changeDetection: ChangeDetectionStrategy.OnPush,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    MatIconModule,
    MatSelectModule,
    SubmitButtonComponent
  ],
  templateUrl: './register-form.html',
  styleUrl: './register-form.css'
})
export class RegisterFormComponent {
  private fb = inject(FormBuilder);

  loading = input<boolean>(false);
  submitRegister = output<RegisterRequest>();

  showPassword = signal(false);
  showConfirmPassword = signal(false);

  registerForm: FormGroup = this.fb.group({
    full_name: ['', [Validators.required]],
    username: ['', [Validators.required]],
    email: ['', [Validators.required, Validators.email]],
    role: ['', [Validators.required]],
    password: ['', [Validators.required, Validators.minLength(6)]],
    confirmPassword: ['', [Validators.required]]
  }, { validators: this.passwordMatchValidator });

  passwordMatchValidator(form: FormGroup) {
    const password = form.get('password');
    const confirmPassword = form.get('confirmPassword');

    if (password && confirmPassword && password.value !== confirmPassword.value) {
      return { passwordMismatch: true };
    }
    return null;
  }

  togglePasswordVisibility(): void {
    this.showPassword.set(!this.showPassword());
  }

  toggleConfirmPasswordVisibility(): void {
    this.showConfirmPassword.set(!this.showConfirmPassword());
  }

  onSubmit(): void {
    if (this.registerForm.valid) {
      const { confirmPassword, ...registerData } = this.registerForm.value;
      this.submitRegister.emit(registerData);
    }
  }
}

import { Component, signal, inject, ChangeDetectionStrategy } from '@angular/core';
import { Router, RouterLink } from '@angular/router';
import { CommonModule } from '@angular/common';
import { firstValueFrom } from 'rxjs';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatSnackBarModule, MatSnackBar } from '@angular/material/snack-bar';
import { RegisterFormComponent } from '../../components/register-form/register-form';
import { AuthService } from '../../services/auth.service';
import { RegisterRequest } from '../../../../core/models/user.model';

@Component({
  selector: 'app-register-page',
  changeDetection: ChangeDetectionStrategy.OnPush,
  imports: [
    CommonModule,
    RegisterFormComponent,
    MatCardModule,
    MatButtonModule,
    MatIconModule,
    MatSnackBarModule,
    RouterLink
  ],
  templateUrl: './register-page.html',
  styleUrl: './register-page.css'
})
export class RegisterPageComponent {
  private authService = inject(AuthService);
  private router = inject(Router);
  private snackBar = inject(MatSnackBar);

  loading = signal(false);

  async handleRegister(registerData: RegisterRequest): Promise<void> {
    this.loading.set(true);

    try {
      const response = await firstValueFrom(this.authService.register(registerData));
      this.authService.setSession(response);

      this.snackBar.open('춰Cuenta creada exitosamente! Bienvenido', 'Cerrar', {
        duration: 3000,
        panelClass: ['success-snackbar']
      });

      this.router.navigate(['/dashboard']);
    } catch (error: any) {
      console.error('Error en registro:', error);

      this.snackBar.open(
        error.message || 'Error al crear la cuenta. Verifica los datos e intenta nuevamente.',
        'Cerrar',
        {
          duration: 5000,
          panelClass: ['error-snackbar']
        }
      );
    } finally {
      this.loading.set(false);
    }
  }
}

import { Component, input, output } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';

@Component({
  selector: 'app-submit-button',
  imports: [MatButtonModule, MatIconModule, MatProgressSpinnerModule],
  styleUrl: './submit-button.css',
  template: `
    <button
      mat-raised-button
      type="submit"
      class="submit-button"
      [disabled]="disabled() || loading()"
      [attr.aria-describedby]="loading() ? loadingDescriptionId() : descriptionId()"
      (click)="onClick.emit()"
    >
      @if (loading()) {
        <mat-progress-spinner
          diameter="20"
          class="inline-spinner"
          mode="indeterminate"
          aria-hidden="true">
        </mat-progress-spinner>
        <span>{{ loadingText() }}</span>
        @if (loadingDescriptionId()) {
          <span [id]="loadingDescriptionId()" class="sr-only">
            {{ loadingDescription() }}
          </span>
        }
      } @else {
        @if (icon()) {
          <mat-icon aria-hidden="true">{{ icon() }}</mat-icon>
        }
        <span>{{ text() }}</span>
        @if (descriptionId()) {
          <span [id]="descriptionId()" class="sr-only">
            {{ description() }}
          </span>
        }
      }
    </button>
  `,
  styles: []
})
export class SubmitButtonComponent {
  // Required inputs
  text = input.required<string>();

  // Optional inputs
  disabled = input<boolean>(false);
  loading = input<boolean>(false);
  icon = input<string>('');
  loadingText = input<string>('Procesando...');

  // Accessibility inputs
  description = input<string>('');
  descriptionId = input<string>('');
  loadingDescription = input<string>('');
  loadingDescriptionId = input<string>('');

  // Output
  onClick = output<void>();
}

